pragma solidity ^0.8.0;

import '../libraries/FullMath.sol';
import '../libraries/TickMath.sol';
import '../libraries/SafeCast.sol';
import '../libraries/PoolAddressTokamak.sol';
import '../libraries/Path.sol';

// import "hardhat/console.sol";
interface IIIUniswapV3Pool {

    function observe(uint32[] calldata secondsAgos)
        external
        view
        returns (int56[] memory tickCumulatives, uint160[] memory secondsPerLiquidityCumulativeX128s);

    function slot0()
        external
        view
        returns (
            uint160 sqrtPriceX96,
            int24 tick,
            uint16 observationIndex,
            uint16 observationCardinality,
            uint16 observationCardinalityNext,
            uint8 feeProtocol,
            bool unlocked
        );
}

/// @title Oracle library
/// @notice Provides functions to integrate with V3 pool oracle
library OracleLibrary {
    using Path for bytes;
    using SafeCast for uint256;

    /// @notice Fetches time-weighted average tick using Uniswap V3 oracle
    /// @param pool Address of Uniswap V3 pool that we want to observe
    /// @param period Number of seconds in the past to start calculating time-weighted average
    /// @return timeWeightedAverageTick The time-weighted average tick from (block.timestamp - period) to block.timestamp
    function consult(address pool, uint32 period) public view returns (int24 timeWeightedAverageTick) {
        require(period != 0, 'BP');

        uint32[] memory secondAgos = new uint32[](2);
        secondAgos[0] = period;
        secondAgos[1] = 0;

        (int56[] memory tickCumulatives, ) = IIIUniswapV3Pool(pool).observe(secondAgos);
        int56 tickCumulativesDelta = tickCumulatives[1] - tickCumulatives[0];

        timeWeightedAverageTick = int24(tickCumulativesDelta / int56( int32(period) ));

        // Always round to negative infinity
        if (tickCumulativesDelta < 0 && (tickCumulativesDelta % int56( int32(period) ) != 0)) timeWeightedAverageTick--;
    }

    /// @notice Given a tick and a token amount, calculates the amount of token received in exchange
    /// @param tick Tick value used to calculate the quote
    /// @param baseAmount Amount of token to be converted
    /// @param baseToken Address of an ERC20 token contract used as the baseAmount denomination
    /// @param quoteToken Address of an ERC20 token contract used as the quoteAmount denomination
    /// @return quoteAmount Amount of quoteToken received for baseAmount of baseToken
    function getQuoteAtTick(
        int24 tick,
        uint128 baseAmount,
        address baseToken,
        address quoteToken
    ) public pure returns (uint256 quoteAmount) {
        uint160 sqrtRatioX96 = TickMath.getSqrtRatioAtTick(tick);

        // Calculate quoteAmount with better precision if it doesn't overflow when multiplied by itself
        if (sqrtRatioX96 <= type(uint128).max) {
            uint256 ratioX192 = uint256(sqrtRatioX96) * sqrtRatioX96;
            quoteAmount = baseToken < quoteToken
                ? FullMath.mulDiv(ratioX192, baseAmount, 1 << 192)
                : FullMath.mulDiv(1 << 192, baseAmount, ratioX192);
        } else {
            uint256 ratioX128 = FullMath.mulDiv(sqrtRatioX96, sqrtRatioX96, 1 << 64);
            quoteAmount = baseToken < quoteToken
                ? FullMath.mulDiv(ratioX128, baseAmount, 1 << 128)
                : FullMath.mulDiv(1 << 128, baseAmount, ratioX128);
        }
    }

    function getPool(
        address factory,
        address tokenA,
        address tokenB,
        uint24 fee
    ) public pure returns (address) {
        return (
            PoolAddressTokamak.computeAddress(
                factory,
                PoolAddressTokamak.getPoolKey(tokenA, tokenB, fee),
                PoolAddressTokamak.POOL_INIT_CODE_HASH));
    }

    function getPool(
        address factory,
        address tokenA,
        address tokenB,
        uint24 fee,
        bytes32 initCodeHash
    ) public pure returns (address) {
        return (PoolAddressTokamak.computeAddress(factory, PoolAddressTokamak.getPoolKey(tokenA, tokenB, fee), initCodeHash));
    }

    function getOutAmounts(address factory, bytes memory _path, uint256 _amountIn, uint32 oracleConsultPeriod)
        public view returns (uint256 amountOut)
    {
        // uint256 count = _path.numPools();
        uint256 i = 0;
        uint256 amountIn = _amountIn;
        bytes memory path = _path;
        while (true) {
            (address tokenIn, address tokenOut, uint24 fee) = path.decodeFirstPool();

            address pool = getPool(factory, tokenIn, tokenOut, fee);
            // the outputs of prior swaps become the inputs to subsequent ones
            uint256 _amountOut = getQuoteAtTick(
                consult(pool, oracleConsultPeriod),
                uint128(amountIn),
                tokenIn,
                tokenOut
            );

            amountIn = _amountOut;
            i++;

            // decide whether to continue or terminate
            if (path.hasMultiplePools()) {
                path = path.skipToken();
            } else {
                return (amountIn);
            }
        }
    }

    function getOutAmountsCurTick(address factory, bytes memory _path, uint256 _amountIn)
        public view returns (uint256 amountOut)
    {

        uint256 amountIn = _amountIn;
        bytes memory path = _path;
        while (true) {
            (address tokenIn, address tokenOut, uint24 fee) = path.decodeFirstPool();

            address pool = getPool(factory, tokenIn, tokenOut, fee);

            (,int24 tick,,,,,) = IIIUniswapV3Pool(pool).slot0();

            // the outputs of prior swaps become the inputs to subsequent ones
            uint256 _amountOut = getQuoteAtTick(
                tick,
                uint128(amountIn),
                tokenIn,
                tokenOut
            );

            amountIn = _amountOut;

            // decide whether to continue or terminate
            if (path.hasMultiplePools()) {
                path = path.skipToken();
            } else {
                return (amountIn);
            }
        }
    }

}
